var Intro, DappsSumm, End, FetchAddress, FileSumm;

fetch('/SetData/intro.txt')
  .then(response => response.text())
  .then((data) => {
    Intro = data;
  })

  fetch('/SetData/end.txt')
    .then(response => response.text())
    .then((data) => {
      End = data;
    })


export function fetchDappData(value){
   FetchAddress = value;
   fetch(FetchAddress)
    .then(response => response.text())
    .then((data) => {
    DappsSumm += data;
    })
    }

export function getSelectedCheckboxValues(){
     DappsSumm = ""
    const checkboxes = document.querySelectorAll("input[type='checkbox']:checked");
        var values = [];
        checkboxes.forEach((checkbox) => {
            values.push(checkbox.value);
        });
       values.forEach(fetchDappData);
     }

export function gather(){
   return FileSumm = Intro + DappsSumm + End;
 }

export function download_file(name, contents, mime_type) {
       mime_type = mime_type || "text/plain";

       var blob = new Blob([contents], {type: mime_type});

       var dlink = document.createElement('a');
       dlink.download = name;
       dlink.href = window.URL.createObjectURL(blob);
       dlink.onclick = function(e) {
           // revokeObjectURL needs a delay to work properly
           var that = this;
           setTimeout(function() {
               window.URL.revokeObjectURL(that.href);
           }, 1500);
       };

       dlink.click();
       dlink.remove();
   }

   function download(filename, text) {
       var element = document.createElement('a');
       element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
       element.setAttribute('download', filename);

       element.style.display = 'none';
       document.body.appendChild(element);

       element.click();

       document.body.removeChild(element);
   }

   // Start file download.
   document.getElementById("dwn-btn").addEventListener("click", function(){
       // Generate download of hello.txt file with some content
       var text = document.getElementById("text-val").value;
       var filename = "hello.txt";

       download(filename, text);
   }, false);
