import React, { useState } from 'react';

import BootstrapTable from 'react-bootstrap-table-next';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';


const { SearchBar } = Search;

const products = [
      {
        id: 1,
        name: 'TV',
        'price': 1000
      },
      {
        id: 2,
        name: 'Mobile',
        'price': 500
      },
      {
        id: 3,
        name: 'Book',
        'price': 20
      },
    ]

const columns = [{
  dataField: 'id',
  text: 'Product ID',
    sort: true
}, {
  dataField: 'name',
  text: 'Product Name',
    sort: true,
    filter: textFilter()

}, {
  dataField: 'price',
  text: 'Product Price'
}]

const defaultSorted = [{
  dataField: 'name',
  order: 'desc'
}];

const SearchTable= () => (
  <ToolkitProvider
    keyField="id"
    data={ products }
    columns={ columns }
    search
  >
    {
      props => (
        <div>
          <h3>Input something at below input field:</h3>
          <SearchBar { ...props.searchProps } />
          <hr />
          <BootstrapTable
            { ...props.baseProps }
          />
        </div>
      )
    }
  </ToolkitProvider>


);
  export default SearchTable;
