import React, { useState } from 'react';

import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';


function download(filename, text) {
  var element = document.createElement('a');
  element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
  element.setAttribute('download', filename);

  element.style.display = 'none';
  document.body.appendChild(element);

  element.click();

  document.body.removeChild(element);
}



const EtIDNav = () => (
<>
<Navbar bg="dark" variant="dark">
<Navbar.Brand href="#home">
  <img
    alt=""
    src="/logo.svg"
    width="30"
    height="30"
    className="d-inline-block align-top"
  />{' '}
  Eth-ter-I-Die Add logo
</Navbar.Brand>
</Navbar>
</>
);

  //check if need to justufy content below!
const EtIDIntro = () => (
  <Container fluid className="p-3">

  <Row className="justify-content-md-center">
  <Col>centred image and intro text goes here

      </Col>
  </Row>
<br />
    <Row className="justify-content-md-center">
    <Col>
      <Accordion>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">
              Story Time!
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body>Put in story here of ether it die here </Card.Body>
          </Accordion.Collapse>
        </Card>
        </Accordion>
      </Col>

      </Row>
      <br />
      <Row>
      <Col>centred How to use text here
      </Col>
      </Row>

  </Container>

  );

const EtIDSelection= () => (
 <Container>
 <Row>
 <Col>
 Wallets
 </Col>
 </Row>
     <Row>
     <Col>
     <ToggleButtonGroup type='checkbox' name='Wallets'>
    <ToggleButton variant="outline-dark" value={'hip_hop'}>MetaMask</ToggleButton>
      </ToggleButtonGroup>
     </Col>

      <Col>
      <ToggleButtonGroup type='checkbox' name='Wallets' >
     <ToggleButton variant="outline-dark" value={'hip_hop'} disabled>Ledger</ToggleButton>
       </ToggleButtonGroup>
     </Col>

     </Row>
     <br />
     <Row>
     <Col>
     Exchanges
     </Col>
     </Row>
         <Row>
         <Col>
         <ToggleButtonGroup type='checkbox' name='Exchanges'>
        <ToggleButton variant="outline-dark" value={'hip_hop'}>UniSwap</ToggleButton>
          </ToggleButtonGroup>
         </Col>

          <Col>
          <ToggleButtonGroup type='checkbox' name='Wallets' >
         <ToggleButton variant="outline-dark" value={'hip_hop'} disabled>OpenSea</ToggleButton>
           </ToggleButtonGroup>
         </Col>

         </Row>
       </Container>
);


const EtIDDownload= () => (
 <Container>
 <Row>
 <Col>
 <button onClick={download("hello.txt","This is the content of my file :)")}>Take the shot!</button>

 </Col>
 </Row>
 </Container>
 );

const App = () => (
    <>
      <EtIDNav />
      <EtIDIntro />
      <EtIDSelection />
      <EtIDDownload />

   </>
    );





export default App;
