import React, { useState } from 'react';

import BootstrapTable from 'react-bootstrap-table-next';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import paginationFactory from 'react-bootstrap-table2-paginator';


const { SearchBar, ClearSearchButton } = Search;

const products = [
      {
        id: 1,
        name: 'TV',
        'price': 1000,
        link:'/DappData/metamask.txt'
      },
      {
        id: 2,
        name: 'Mobile',
        'price': 500,
        link : '/DappData/metamask.txt'
      },
      {
        id: 3,
        name: 'Book',
        'price': 20,
        link : '/DappData/metamask.txt'
      },
    ]

    const columns = [{
      dataField: 'id',
      text: 'Product ID',
      hidden: true
    }, {
      dataField: 'name',
      text: 'Product Name'
    }, {
      dataField: 'price',
      text: 'Product Price'
    }, {
      dataField: 'link',
        text: 'File',
        formatter: (rowContent, row) => {
          return (
            <a href="#">Check</a>
          )

      }}]

      const linkFormatter = (cell, row, rowIndex) => {
        return (
          <a href={cell} target="_blank">
            See mail
          </a>
        );
      };



    const options = {
            pageStartIndex: 1,
            firstPageText: 'First',
            prePageText: 'Back',
            nextPageText: 'Next',
            lastPageText: 'Last',
            nextPageTitle: 'First page',
            prePageTitle: 'Pre page',
            firstPageTitle: 'Next page',
            lastPageTitle: 'Last page',
            showTotal: true,
            sizePerPageList: [{
              text: '5', value: 5
            }, {
              text: '10', value: 10
            }, {
              text: '25', value: 25
              }]
          };

const SearchTable= () => (

<ToolkitProvider
  keyField="id"
  data={ products }
  columns={ columns }
  search

>
  {
    props => (
      <div>
        <h3>Input something at below input field:</h3>
        <SearchBar { ...props.searchProps } />
        <ClearSearchButton { ...props.searchProps } />
        <hr />
        <BootstrapTable
          { ...props.baseProps }
          striped
          hover
          pagination={ paginationFactory(options) }
        />
      </div>
    )
  }
</ToolkitProvider>


);
  export default SearchTable;
