import React, { useState } from 'react';

import { Button,Modal } from 'react-bootstrap'


import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
//import Button from 'react-bootstrap/Button';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert'
//import Modal from 'react-bootstrap/Modal'

import { saveAs } from 'file-saver';
import { introAndEndData, returnintroEndStatus, remvoveIntroAndEndData, fetchDappData, getSelectedCheckboxValues, gather, download_FS} from './core.js';

window.onload = function() {
  introAndEndData();
};



const EtIDNav = () => (
<>
<Navbar bg="dark" variant="dark">
<Navbar.Brand href="#home">
  <img
    alt=""
    src="/logo.svg"
    width="30"
    height="30"
    className="d-inline-block align-top"
  />{' '}
  Eth-ter-I-Die Add logo
</Navbar.Brand>
</Navbar>
</>
);

  //check if need to justufy content below!
const EtIDIntro = () => (
  <Container fluid className="p-3">

  <Row className="justify-content-md-center">
  <Col>centred image and intro text goes here

      </Col>
  </Row>
<br />

      <br />
      <Row>
      <Col>centred How to use text here
      </Col>
      </Row>

  </Container>

  );

const EtIDSelection= () => (
 <Container>
 <Row>
 <Col>
 Wallets
 </Col>
 </Row>
     <Row>
     <Col>
     <ToggleButtonGroup type='checkbox' name='Wallets'>
    <ToggleButton variant="outline-dark" onChange={getSelectedCheckboxValues} value='/DappData/metamask.txt'>MetaMask</ToggleButton>
      </ToggleButtonGroup>
     </Col>

      <Col>
      <ToggleButtonGroup type='checkbox' name='Wallets' >
     <ToggleButton variant="outline-dark" onChange={getSelectedCheckboxValues} value='/DappData/ledger.txt'>Ledger</ToggleButton>
       </ToggleButtonGroup>
     </Col>
          </Row>
     <br />
     <Row>
     <Col>
     Exchanges
     </Col>
     </Row>
         <Row>
         <Col>
         <ToggleButtonGroup type='checkbox' name='Exchanges'>
        <ToggleButton variant="outline-dark" value='/DappData/ledger.txt' disabled>UniSwap</ToggleButton>
          </ToggleButtonGroup>
         </Col>

          <Col>
          <ToggleButtonGroup type='checkbox' name='Wallets' >
         <ToggleButton variant="outline-dark" value='/DappData/metamask.txt' disabled>OpenSea</ToggleButton>
           </ToggleButtonGroup>
         </Col>

         </Row>
         <br />
       </Container>
);


const EtIDDownload= () => (

 <Container>
 <Row>
 <Col>
  <ModalApp />

 </Col>
 </Row>
 <br />
 <Row className="justify-content-md-center">
 <Col>
   <Accordion>
     <Card>
       <Card.Header>
         <Accordion.Toggle as={Button} variant="link" eventKey="0">
           Advanced Settings
         </Accordion.Toggle>
       </Card.Header>
       <Accordion.Collapse eventKey="0">
         <Card.Body>
         <br />
         <Button variant="success" type="submit" onClick={() => { introAndEndData() }}>Include Intro and End text from Download (Default)</Button>
         <Button variant="danger" type="submit" onClick={() => { remvoveIntroAndEndData() }}>Exclude Intro and End text from Download</Button>

         </Card.Body>
       </Accordion.Collapse>
     </Card>
     </Accordion>
   </Col>

   </Row>
 </Container>
 );

const App = () => (



    <>
      <EtIDNav />
      <EtIDIntro />
      <EtIDSelection />
      <EtIDDownload />

   </>

    );

    function ModalApp() {
      const [modalShow, setModalShow] = React.useState(false);

      return (
        <>
          <Button variant="primary" onClick={() => setModalShow(true)}>
            Download File
          </Button>

          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
        </>
      );
    }

    function MyVerticallyCenteredModal(props) {
      return (
        <Modal
          {...props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Modal heading
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>Centered Modal</h4>
            <p>
              Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
              dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
              consectetur ac, vestibulum at eros.
            </p>

          </Modal.Body>
          <Modal.Footer>
          <Button variant="primary" type="submit" onClick={() => { download_FS("Ether-I-Die.txt", gather()) }}>Agree and Download</Button>
            <Button variant="secondary" onClick={props.onHide}>Close</Button>
          </Modal.Footer>
        </Modal>
      );
    }
export default App;
