import React, { useState } from 'react';

import { Button, Modal, Container, Card, Row, Col, Image, Accordion, ToggleButton, ToggleButtonGroup, Form, Alert, Toast } from 'react-bootstrap'

import { saveAs } from 'file-saver';
import { introAndEndData, returnintroEndStatus, remvoveIntroAndEndData, fetchDappData, getSelectedCheckboxValues, gather, download_FS} from './core.js';


import EtIDNav from './EtIDNav.js';
import EtIDSelection from './EtIDSelection.js';
import EtIDDownload from './EtIDDownload.js'


window.onload = function() {
  introAndEndData();
};



  //check if need to justufy content below!
const EtIDIntro = () => (
  <Container fluid className="p-3">

  <Row className="justify-content-md-center">
  <Col>centred image and intro text goes here

      </Col>
  </Row>
<br />

      <br />
      <Row>
      <Col>centred How to use text here
      </Col>
      </Row>

  </Container>

  );


const EtIDAdvSet= () => (

 <Container>
 <Row className="justify-content-md-center">
 <Col>
   <Accordion>
     <Card>
       <Card.Header>
         <Accordion.Toggle as={Button} variant="link" eventKey="0">
           Advanced Settings
         </Accordion.Toggle>
       </Card.Header>
       <Accordion.Collapse eventKey="0">
         <Card.Body>
         <br />
         <Button variant="success" type="submit" onClick={() => { introAndEndData() }}>Include Intro and End text from Download (Default)</Button>
         <Button variant="danger" type="submit" onClick={() => { remvoveIntroAndEndData() }}>Exclude Intro and End text from Download</Button>

         </Card.Body>
       </Accordion.Collapse>
     </Card>
     </Accordion>
   </Col>

   </Row>
 </Container>
 );

const App = () => (



    <>
      <EtIDNav />
      <EtIDIntro />
      <EtIDSelection />
      <EtIDDownload />
      <EtIDAdvSet/>

   </>
    );


export default App;
